import * as actionTypes from "./actionTypes";
import axios from 'axios'

let initialState: TodoState = {todos: []};

axios.get(`http://localhost:3001/api/v1/todos`)
.then(res => {
  initialState['todos'] = res.data;
  return res.data;

});

console.log("data2",initialState);

initialState["todos"] =  [
    {
      id: 1,
      title: "post 1",
      description:
        "Quisque cursus, metus vitae pharetra Nam libero tempore, cum soluta nobis est eligendi",
        dueDate: new Date(),
        priority: 1
    },
    {
      id: 2,
      title: "post 2",
      description:
        "Harum quidem rerum facilis est et expedita distinctio quas molestias excepturi sint",
        dueDate: new Date(),
        priority: 2
    },
  ];

const reducer = (
    state: TodoState = initialState,
    action: TodoAction
  ): TodoState => {
    switch (action.type) {
      case actionTypes.ADD_TODO:
        const newTodo: ITodo = {
          id: Math.random(), // not really unique
          title: action.todo.title,
          description: action.todo.description,
          dueDate: action.todo.dueDate,
          priority: action.todo.priority
        }
        return {
          ...state,
          todos: state.todos.concat(newTodo),
        }
      case actionTypes.REMOVE_TODO:
        const updatedTodos: ITodo[] = state.todos.filter(
          todo => todo.id !== action.todo.id
        )
        return {
          ...state,
          todos: updatedTodos,
        }
    }
    return state
  }

  export default reducer
