import * as actionTypes from "./actionTypes";
import axios from 'axios';
export function addTodo(todo: ITodo) {
  const action: TodoAction = {
    type: actionTypes.ADD_TODO,
    todo,
  }

  return simulateHttpRequest(action)
}

export function getTodos(todo: ITodo[]) {
  axios.get(`http://localhost:3001/api/v1/todos`)
  .then(res => {
    // initialState['todos'] = ¸;
    const todo = res.data;
    const action: TodoAction = {
      type: actionTypes.GET_TODO,
      todo,
    }

    return simulateHttpRequest(action)

  });

}

export function removeTodo(todo: ITodo) {
  const action: TodoAction = {
    type: actionTypes.REMOVE_TODO,
    todo,
  }
  return simulateHttpRequest(action)
}

export function simulateHttpRequest(action: TodoAction) {
  return (dispatch: DispatchType) => {
    setTimeout(() => {
      dispatch(action)
    }, 500)
  }
}
